CREATE OR REPLACE VIEW v_participant_report AS SELECT pt.name, pt.recruitment_number, pt.status_in_study, pr.date AS recruitment_date, pt.fieldcenter, ci.street, ci.number, ci.complement, ci.neighborhood, ci.city, ci.state, ci.zipcode, ci.observation, ci.phone, ci.phone2, (SELECT max(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'LAP'::text))) AS last_lapa_date, (SELECT MAX(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'LIU'::text))) AS last_liua_date, v.still_pregnant AS lapa1, v.expected_date_of_birth AS dpp, v.date_of_birth AS dp, v.takes_insulin, v.used_insulin_during_pregnancy, v.should_receive_breastfeeding_orientation AS lapa_inf_breastfeeding, v.should_receive_breastfeeding_orientation_comment AS lapa_inf_breastfeeding_comment, v.basal1_rcp_date, v.basal1_date, v.breastfeeding_call_one, v.call_one_date, v.ttg_exam_date, array_to_string(ARRAY (SELECT gc.comment FROM general_comment gc WHERE pt.id = gc.participant_id AND gc.deleted = FALSE ORDER BY gc.id), ','::text) AS general_comments, ((SELECT count(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'LIU'::text)))) > 0 AS has_liua, ((SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'LID'::text)))) > 0 AS has_lida, (SELECT MAX(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'TTG'::text))) AS last_ttga_date, v.last_fasting_glycemia_result, v.last_post_120_glycemia_result, (SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'TTG'::text))) AS ttga_count, (SELECT acea6 FROM (SELECT acea6, activity.realization_date FROM coorte.ace_a acea JOIN activity ON activity.id = acea.activity_id WHERE pt.id = activity.participant_id ) AS result ORDER BY realization_date DESC LIMIT 1) AS return_accelerometer, (SELECT MAX(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'LID'::text))) AS last_lida_date, (SELECT MAX(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'RPN'::text))) AS last_rpna_date, (SELECT MAX(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'DSO'::text))) AS last_dsoa_date, (SELECT MAX(activity.realization_date) AS MAX FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'AAF'::text))) AS basal2_date, ((SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'TCM'::text)))) > 0 AS has_tcma, ( (SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'TCC'::text)))) > 0 AS has_tcc, ((SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'RGP'::text)))) > 0 AS has_rgp, ((SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'TCE'::text)))) > 0 AS has_tce, ((SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'ACE'::text)))) > 0 AS has_ace, ((SELECT COUNT(*) AS COUNT FROM activity WHERE activity.participant_id = pt.id AND activity.status::text = 'FINALIZED'::text AND (activity.activity_type_id IN (SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'BIA'::text)))) > 0 AS has_bia, (SELECT (EXISTS (SELECT up.id, up.file_name, up.file_path, up.original_file_name, up.upload_date, up.activity_id, up.participant_id, up.user_id FROM uploaded_file up WHERE (up.activity_id IN (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'TCC'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)))) AS "exists") AS has_tcc_upload, (SELECT (EXISTS (SELECT up.id, up.file_name, up.file_path, up.original_file_name, up.upload_date, up.activity_id, up.participant_id, up.user_id FROM uploaded_file up WHERE (up.activity_id IN (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'RGP'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)))) AS "exists") AS has_rgp_upload, (SELECT (EXISTS (SELECT up.id, up.file_name, up.file_path, up.original_file_name, up.upload_date, up.activity_id, up.participant_id, up.user_id FROM uploaded_file up WHERE (up.activity_id IN (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'TCE'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)))) AS "exists") AS has_tce_upload, (SELECT (EXISTS (SELECT up.id, up.file_name, up.file_path, up.original_file_name, up.upload_date, up.activity_id, up.participant_id, up.user_id FROM uploaded_file up WHERE (up.activity_id IN (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'ACE'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)))) AS "exists") AS has_ace_upload, (SELECT (EXISTS (SELECT up.id, up.file_name, up.file_path, up.original_file_name, up.upload_date, up.activity_id, up.participant_id, up.user_id FROM uploaded_file up WHERE (up.activity_id IN (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'BIA'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)))) AS "exists") AS has_bia_upload, (SELECT (EXISTS (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'LSM'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)) AS "exists") AS has_lsm, (SELECT (EXISTS (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'LDA'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)) AS "exists") AS has_lda, (SELECT (EXISTS (SELECT activity.id FROM activity JOIN activity_type TYPE ON activity.activity_type_id = TYPE.id WHERE TYPE.activity_shortname::text = 'LUA'::text AND activity.status::text = 'FINALIZED'::text AND pt.id = activity.participant_id)) AS "exists") AS has_lua, v.breastfeeding_one_year, v.call_one_year, v.glycemia_fasting_call_one_year, v.glycemia_pos_call_one_year, v.ttg_one_year, v.breastfeeding_six_months, v.call_six_months, v.glycemia_fasting_six_months, v.glycemia_pos_six_months, v.ttg_six_months, pt."group" AS clinical_trial_group FROM participant pt LEFT JOIN participant_registry pr ON pt.id = pr.participant_id LEFT JOIN variables v ON v.participant_id = pt.id LEFT JOIN contact ct ON ct.participant_id = pt.id AND (pt.main_contact IS NOT NULL AND ct.relation::text = pt.main_contact::text OR pt.main_contact IS NULL AND ct.relation::text = 'PARTICIPANT'::text) LEFT JOIN contact_info ci ON ci.id = ct.contact_info_id;
ALTER TABLE v_participant_report OWNER TO postgres;

CREATE OR REPLACE VIEW lab.v_aliquot_processing AS SELECT to_date(act.realization_date::character(10)::text, 'yyyy-MM-dd'::text) AS aliquoting_date, act.activity_category, p.fieldcenter AS field_center, a.quality_control, a.aliquot_type, a.aliquot_moment, t.tube_type, count(*) AS count FROM lab.aliquot a JOIN lab.tube t ON t.id = a.tube_id JOIN activity act ON act.id = a.aliquoting_activity_id JOIN participant p ON p.id = act.participant_id WHERE a.code IS NOT NULL GROUP BY to_date(act.realization_date::character(10)::text, 'yyyy-MM-dd'::text), act.activity_category, p.fieldcenter, a.quality_control, a.aliquot_type, a.aliquot_moment, t.tube_type ORDER BY to_date(act.realization_date::character(10)::text, 'yyyy-MM-dd'::text), act.activity_category, p.fieldcenter, a.quality_control, a.aliquot_type, a.aliquot_moment, t.tube_type;
ALTER TABLE lab.v_aliquot_processing OWNER TO postgres;

CREATE OR REPLACE VIEW lab.v_aliquot_type_report AS SELECT act.realization_date, prt.fieldcenter, alq.code, alq.aliquot_type, alq.aliquot_moment FROM lab.aliquot alq JOIN activity act ON act.id = alq.aliquoting_activity_id JOIN participant prt ON prt.id = act.participant_id WHERE (act.status::text <> ALL (ARRAY['DELETED'::character varying, 'NOT_INITIALIZED'::character varying]::text[])) AND alq.code IS NOT NULL;
ALTER TABLE lab.v_aliquot_type_report OWNER TO postgres;

CREATE OR REPLACE VIEW extraction.v_cad_a AS SELECT pt.recruitment_number AS numero_recrutamento, pr.date AS data_entrevista, us.code AS entrevistador, pt.birthdate AS data_nascimento, pt.health_record_id AS prontuario FROM participant pt JOIN participant_registry pr ON pr.participant_id = pt.id JOIN "user" us ON us.id = pr.interviewer_id  ORDER BY pt.recruitment_number;
ALTER TABLE extraction.v_cad_a OWNER TO postgres;
GRANT ALL ON TABLE extraction.v_cad_a TO postgres;
GRANT SELECT ON TABLE extraction.v_cad_a TO estatistica;

