CREATE OR REPLACE VIEW v_participantes AS SELECT p.recruitment_number AS id, p.name AS nome, p.birthdate AS data_nascimento, p.fieldcenter AS centro, 'RECRUITMENT'::character varying(20) AS fase, COALESCE(v.ttg_count, 0) > 0 AS ttg_realizado, ci.phone AS telefone, v.expected_date_of_birth::date AS data_provavel_parto, v.date_of_birth::date AS data_parto, v.takes_insulin AS insulina, CASE WHEN prta.prta7a::text = 'YES'::text OR prta.prta7b::text = 'YES'::text THEN true ELSE false END AS outro_antidiabetico, CASE COALESCE(gstb.gstb23b, gsta.gsta23b) WHEN 'YES'::text THEN true ELSE false END AS pre_eclampsia FROM participant p JOIN variables v ON v.participant_id = p.id LEFT JOIN contact c ON c.participant_id = p.id AND c.relation::text = 'PARTICIPANT'::text LEFT JOIN contact_info ci ON ci.id = c.contact_info_id LEFT JOIN activity a1 ON a1.participant_id = p.id AND a1.status::text = 'FINALIZED'::text AND a1.activity_type_id = (( SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'PRT'::text AND activity_type.version::text = 'A'::text)) LEFT JOIN activity a11 ON a11.participant_id = p.id AND a11.status::text = 'FINALIZED'::text AND a11.realization_date > a1.realization_date AND a11.activity_type_id = (( SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'PRT'::text AND activity_type.version::text = 'A'::text)) LEFT JOIN coorte.prt_a prta ON prta.activity_id = a1.id LEFT JOIN activity a2 ON a2.participant_id = p.id AND a2.status::text = 'FINALIZED'::text AND a2.activity_type_id = (( SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'GST'::text AND activity_type.version::text = 'A'::text)) LEFT JOIN activity a22 ON a22.participant_id = p.id AND a22.status::text = 'FINALIZED'::text AND a22.realization_date > a2.realization_date AND a22.activity_type_id = (( SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'GST'::text AND activity_type.version::text = 'A'::text)) LEFT JOIN coorte.gst_a gsta ON gsta.activity_id = a2.id LEFT JOIN activity a3 ON a3.participant_id = p.id AND a3.status::text = 'FINALIZED'::text AND a3.activity_type_id = (( SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'GST'::text AND activity_type.version::text = 'B'::text)) LEFT JOIN activity a33 ON a33.participant_id = p.id AND a33.status::text = 'FINALIZED'::text AND a33.realization_date > a3.realization_date AND a33.activity_type_id = (( SELECT activity_type.id FROM activity_type WHERE activity_type.activity_shortname::text = 'GST'::text AND activity_type.version::text = 'B'::text)) LEFT JOIN coorte.gst_b gstb ON gstb.activity_id = a3.id WHERE a11.id IS NULL AND a22.id IS NULL AND a33.id IS NULL;
